<template>
    <div>
        <header class="section-header">
            <button class="btn btn btn-primary btn-sm btn-assign-item" @click="openStationTeamCreateModal()">
                <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-plus-lg" viewBox="0 0 16 16">
                    <path d="M8 0a1 1 0 0 1 1 1v6h6a1 1 0 1 1 0 2H9v6a1 1 0 1 1-2 0V9H1a1 1 0 0 1 0-2h6V1a1 1 0 0 1 1-1z"></path>
                </svg>
                Assign Team
            </button>
        </header>
        <div>
            <b-overlay :show="showInprogress" opacity="0.6" no-wrap></b-overlay>
            <table class="table table-bordered settings-station-table" v-if="showStationTeamsTable">
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Start Time</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                <tr v-for="stationTeam in stationTeams" :key="stationTeam.id">
                    <td>{{stationTeam.name}}</td>
                    <td>{{stationTeam.start_time | formatDateTime}}</td>
                    <td>
                        <a class="btn btn-danger btn-sm" @click.prevent="openStationTeamDeleteModal(stationTeam)">
                            <b-icon icon="trash" class="pb-sm-1" font-scale="1.30"></b-icon>
                                REMOVE
                        </a>
                    </td>
                </tr>
                </tbody>
            </table>
        </div>


        <Modal v-if="showStationTeamCreateModal === true" @close="closeStationTeamModal()" >
            <template v-slot:header>
                <div>
                    <h5>ASSIGN STATION TEAM</h5>
                </div>
            </template>
            <template v-slot:content>
                <form>
                    <div class="form-group">
                        <label for="teamSelect">Team</label>
                        <select required id="teamSelect" class="form-control"
                                v-model="selectedTeamId">
                            <option disabled value=""> --Select-- </option>
                            <option v-for="team in teams" :value="team.id" :key="team.id">
                                {{ team.name }}
                            </option>
                        </select>
                        <label for="startTimeSelect" class="mt-3">Select start date and time</label>
                        <VueCtkDateTimePicker id="startTimeSelect" v-model="stationTeam.start_time" :inline="true" format="YYYY-MM-DD HH:mm:ss" formatted="YYYY-MM-DD HH:mm:ss" />
                    </div>
                </form>
            </template>
            <template v-slot:footer>
                <div class="float-end pb-4" style="padding-right: 15px;">
                    <button class="btn btn-outline-danger" @click.prevent="closeStationTeamModal()">CLOSE</button>
                    <button class="btn btn-success ms-3" @click.prevent="createStationTeam()">SUBMIT</button>
                </div>
            </template>
        </Modal>

        <Modal v-if="showStationTeamDeleteModal" @close="closeStationTeamModal()">
            <template v-slot:header>
                <div class="container">
                    Delete Station Team
                </div>
            </template>
            <template v-slot:content>
                <form @submit.prevent="">
                    <p>Are you sure you want to delete the team named <span style="color: darkred">{{selectedStationTeamNameToDelete}}</span>?</p>
                </form>
            </template>
            <template v-slot:footer>
                <div class="float-end pb-4">
                    <button class="btn btn-outline-danger" @click.prevent="closeStationTeamModal()">CLOSE</button>
                    <button class="btn btn-danger ms-3" @click="deleteStationTeam">DELETE</button>
                </div>
            </template>
        </Modal>
    </div>
</template>

<script>

    import stationTeamService from '../../services/StationTeamService';
    import teamService from '../../services/TeamService';
    import toastrService from '../../services/ToastrService';

    export default {
        name: "StationTeam",
        props: {
            stationId: {
                type: Number,
                required: true
            },
        },
        data: () => ({
            stationTeams: [],
            showStationTeamDeleteModal: false,
            showStationTeamCreateModal: false,
            teams: [],
            selectedTeamId: "",
            selectedStationTeamIdToDelete: null,
            selectedStationTeamNameToDelete: null,
            stationTeam: {
                id: null,
                name: null,
                station_id: null,
                team_id: null,
                start_time : null,
            },
            showInprogress: false,
        }),
        watch: {
            stationId: function(newStationId, oldStationId) {
                this.fetchStationTeam();
                this.fetchAllTeams();
            }
        },
        methods: {
            fetchStationTeam() {
                this.showInprogress = true;
                stationTeamService.fetchAll(this.stationId, res => {
                    this.stationTeams = res;
                    console.log(this.stationTeams)
                    this.showInprogress = false;
                }, e => {
                    console.log(e);
                });
            },
            fetchAllTeams() {
                teamService.fetchAllTeams( res => {
                    this.teams = res;
                    console.log(this.teams)
                }, e => {
                    console.log(e);
                });
            },
            openStationTeamCreateModal() {
                this.showStationTeamCreateModal = true;
            },
            closeStationTeamModal() {
                this.showStationTeamCreateModal = false;
                this.showStationTeamDeleteModal = false;
                this.stationTeam.start_time = "";
            },
            createStationTeam() {
                let payload = {
                    'station_id': this.stationId,
                    'team_id': this.selectedTeamId,
                    'start_time' : this.stationTeam.start_time
                };
                stationTeamService.createOrUpdateStationTeam(payload, r => {
                    this.stationTeams = r;
                    this.closeStationTeamModal();
                    toastrService.showSuccessToast('Station team added.');
                }, error => {
                    toastrService.showErrorToast("Validation failed");
                });
            },
            openStationTeamDeleteModal(stationTeam) {
                this.selectedStationTeamIdToDelete = stationTeam.id;
                this.selectedStationTeamNameToDelete = stationTeam.name;
                this.showStationTeamDeleteModal = true;
            },
            deleteStationTeam() {
                stationTeamService.deleteStationTeam(this.selectedStationTeamIdToDelete, r => {
                    this.stationTeams = r;
                    this.closeStationTeamModal();
                    toastrService.showSuccessToast('Deleted station team.')
                }, e => {
                    console.log(e);
                });
                this.fetchStationTeam();
            }
        },
        computed:{
            showStationTeamsTable() {
                return this.stationTeams.length > 0;
            }
        },
        mounted() {
            this.fetchStationTeam();
            this.fetchAllTeams();
        }
    }
</script>

